function handleSelectMovie(id){
  setSelectedId(selectedId => id === selectedId ? null:id)
}

function handleCloseMovie(){
  setSelectedId(null)
}

function handleAddWatch(movie){
  setWatched(watched =>  [...watched,movie])
}


// 
useEffect(function(){

  async function fetchMovies (){

    try{
      setIsLoading(true);
      setError('')
     const res =await fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=${key}&s=${query}`);


    if (!res.ok) throw new Error("Something went Wrong")



    const data =await res.json();
    // setMovies(data.Search); this is getting undefined so it giving error
    if(data.Response === 'False')throw new Error("Movie not found");
     setMovies(data.Search)
     console.log(data.Search)
     console.log(data)
    }
    catch(err){
      console.error(err);
      setError(err.message);

    }
    finally{
      setIsLoading(false);
    }
  }

  if(query.length <3){
    setMovies([]);
    setError("");
    return;
  }

  fetchMovies();

},[query]);


  return (
    <>

    <Navbar >
      <Logo/>
      <Search query={query} setQuery={setQuery}/>
  <NumResult movies={movies}/>
    </Navbar>


     <Main>

    <Box>
    {/* {isLoading?<Loader/>:  <MovieList movies={movies}/>} */}


    {isLoading && <Loader/>}

    {!isLoading && !error && <MovieList movies={movies} onSelectMovie={handleSelectMovie}/>}

    {error && <ErrorMessage message={error}/>}



    </Box>

    {/* <Box element={<MovieList movies={movies}/>}/> */}

    <Box>
      {
        selectedId ? <SelectedMovie selectedId={selectedId} 
                      oncloseMovie={handleCloseMovie}
                      onAddWatched={handleAddWatch}
                      watched={watched}/>:
        <>
        <WatchSummery watched={watched}/>
        <WatchedMovieList watched={watched}/>
        </>

      }

    </Box>

    {/* <Box element={<>
      <WatchSummery watched={watched}/>

       <WatchedMovieList watched={watched}/>
      </>}
    /> */}

    </Main>

    </>
  );
}

//////////////////////////////////////////////

function ErrorMessage({message}){
  return (
    <p className="error"><span>Error</span>
    {message}</p>
  )
}


//////////////////////////////////////////////////////////////


function Loader(){
  return (
    <p className="loader"> Loading...</p>
  )
}



//////////////////////////////////////////////////////////////////


function Navbar({children}){

  return (
    <nav className="nav-bar">
  {children}

  </nav>

  )
}

///////////////////////////////////////////

function Logo(){
  return (
    <div className="logo">
    <span role="img">üçø</span>
    <h1>usePopcorn</h1>
  </div>
  )
}

///////////////////////////////////////////////////

function Search ({query,setQuery}){

  return <input
  className="search"
  type="text"
  placeholder="Search movies..."
  value={query}
  onChange={(e) => setQuery(e.target.value)}
/>
}
////////////////////////////////////////

function NumResult({movies}){
  return(
    <p className="num-results">
    {/* Found <strong>{movies.length}</strong> results */}
    Found <strong> {movies?.length || 0}</strong> results
  </p>

  )
}

///////////////////////////////////////////////////////////

function Main({children}){


 return (
  <main className="main">
  {children}
</main>
 )
}

//////////////////////////////////////////////////////////////
//resusable component
function Box({children}){

  const [isOpen, setIsOpen] = useState(true);
  return (
    <div className="box">
    <button
      className="btn-toggle"
      onClick={() => setIsOpen((open) => !open)}
    >
      {isOpen? "‚Äì" : "+"}
    </button>
    {isOpen && (
//  {children} this is we are creating new object 
children
    )}
  </div>
  )
}
////////////////////////////////////////////////////////////
function MovieList({movies,onSelectMovie}){

  return (

    <ul className="list list-movies">
    {movies?.map((movie) => (
     <Movie movie={movie} onSelectMovie={onSelectMovie}/>
    ))}
  </ul>
  )
}

///////////////////////////////////////////////////////////


function Movie({movie,onSelectMovie}){
  return (
    <li key={movie.imdbID} onClick={()=> onSelectMovie(movie.imdbID)}>
    <img src={movie.Poster} alt={`${movie.Title} poster`} />
    <h3>{movie.Title}</h3>
    <div>
      <p>
        <span>üóì</span>
        <span>{movie.Year}</span>
      </p>
    </div>
  </li>
  )
}

