 import { useEffect, useState } from "react";

const tempMovieData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
  },
  {
    imdbID: "tt0133093",
    Title: "The Matrix",
    Year: "1999",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BNzQzOTk3OTAtNDQ0Zi00ZTVkLWI0MTEtMDllZjNkYzNjNTc4L2ltYWdlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg",
  },
  {
    imdbID: "tt6751668",
    Title: "Parasite",
    Year: "2019",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg",
  },
];

const tempWatchedData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
    runtime: 148,
    imdbRating: 8.8,
    userRating: 10,
  },
  {
    imdbID: "tt0088763",
    Title: "Back to the Future",
    Year: "1985",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BZmU0M2Y1OGUtZjIxNi00ZjBkLTg1MjgtOWIyNThiZWIwYjRiXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",
    runtime: 116,
    imdbRating: 8.5,
    userRating: 9,
  },
];

const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);


//////////////////////////////////////////////////////////////////////
const key='cee5b493'


export default function App() {
//lift up state
const [movies, setMovies] = useState([]);
const [watched, setWatched] = useState([ ]);
// for loading
const [isLoading, setIsLoading]=useState(false)
const query="interstellar"
// 
useEffect(function(){

  async function fetchMovies (){

    setIsLoading(true);
     const res =await fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=${key}&s=${query}`)
    .then(res => res.json())
    .then((data)=>{ // Check if data.Search exists and is an array
    if (data.Search) {
      setMovies(data.Search);
// after loading 
setIsLoading(false)
    } else {
      console.log("No movies found or unexpected response structure", data);
      setMovies([]); // Optional: clear movies if none found
    }
  })
  .catch((error) => console.error("Error fetching movies:", error));


  }

  fetchMovies();

},[]);


  return (
    <>

    <Navbar >
      <Logo/>
      <Search/>
  <NumResult movies={movies}/>
    </Navbar>


     <Main>

    <Box>
    {i}
     <MovieList movies={movies}/>
    </Box>

    {/* <Box element={<MovieList movies={movies}/>}/> */}

    <Box>
    <WatchSummery watched={watched}/>

       <WatchedMovieList watched={watched}/>
    </Box>

    {/* <Box element={<>
      <WatchSummery watched={watched}/>

       <WatchedMovieList watched={watched}/>
      </>}
    /> */}

    </Main>

    </>
  );
}



//////////////////////////////////////////////////////////////////


function Navbar({children}){

  return (
    <nav className="nav-bar">
  {children}

  </nav>

  )
}

///////////////////////////////////////////

function Logo(){
  return (
    <div className="logo">
    <span role="img">üçø</span>
    <h1>usePopcorn</h1>
  </div>
  )
}

///////////////////////////////////////////////////

function Search (){
  const [query, setQuery] = useState("");
  return <input
  className="search"
  type="text"
  placeholder="Search movies..."
  value={query}
  onChange={(e) => setQuery(e.target.value)}
/>
}
////////////////////////////////////////

function NumResult({movies}){
  return(
    <p className="num-results">
    {/* Found <strong>{movies.length}</strong> results */}
    Found <strong> {movies.length}</strong> results
  </p>

  )
}

///////////////////////////////////////////////////////////

function Main({children}){


 return (
  <main className="main">
  {children}
</main>
 )
}

//////////////////////////////////////////////////////////////
//resusable component
function Box({children}){

  const [isOpen, setIsOpen] = useState(true);
  return (
    <div className="box">
    <button
      className="btn-toggle"
      onClick={() => setIsOpen((open) => !open)}
    >
      {isOpen? "‚Äì" : "+"}
    </button>
    {isOpen && (
//  {children} this is we are creating new object 
children
    )}
  </div>
  )
}
////////////////////////////////////////////////////////////
function MovieList({movies}){

  return (

    <ul className="list">
    {movies?.map((movie) => (
     <Movie movie={movie}/>
    ))}
  </ul>
  )
}

///////////////////////////////////////////////////////////


function Movie({movie}){
